        import React, { useState }  from 'react';
        import "./styles.css";
        import { ToastContainer, toast } from 'react-toastify';
        import 'react-toastify/dist/ReactToastify.css';
        import axios from "axios";
        





        export default function Login(props) {


          var err = false;  
          const [afterLogin, setLoginFlag] = useState(false);   
          const [formData, setFormData] = useState({
            email: '',
            password: ''
          });


         const handleChange = (e) => {
            const { name, value } = e.target;
            setFormData((prevFormData) => ({
              ...prevFormData,
              [name]: value
            }));
          };







          const handleSubmit = async (e) => {
            e.preventDefault();
            
            const { email, password} = formData;

            if (email === '' || email ===null || password === '' || password ===null) {
              toast.error("Enter the credentials", {
                position: toast.POSITION.TOP_CENTER,
              });
              err = true;
              return false;
            }
          
          const baseURL = "http://localhost:8080/loginProcessing/"+email;
          try {
          const response = await axios.get(baseURL);
          const data = response.data;

          if(data.length ===0) {
            err = true;
            toast.error("No record found", {
              position: toast.POSITION.TOP_CENTER,
            });
          }
        
          const passDb = data[0].user_password;

          if (password !== passDb) {
            toast.error("Password does not match", {
              position: toast.POSITION.TOP_CENTER,
            });
            err = true;
          }
        } catch (error) {
          console.error(error);
        }
          if(err===false) setLoginFlag(true);
          return true;
        };






        const handlePassword = async (e) => {
          e.preventDefault();
          if (email === '' || email ===null ) {
            toast.error("Enter the registered email", {
              position: toast.POSITION.TOP_CENTER,
            });
            return false;
          }

          const baseURL = "http://localhost:8080/loginProcessing/"+email;
          try {
          const response = await axios.get(baseURL);
          const data = response.data;

          if(data.length ===0) {
            err = true;
            toast.error("No record found", {
              position: toast.POSITION.TOP_CENTER,
            });
          }
        
          const passDbMail = data[0].user_password;

          var nodemailer = require('nodemailer'); 

        var transporter = nodemailer.createTransport({
          service: 'gmail',
          auth: {
          user: 'sk.tech.1997@gmail.com',
          pass: 'Vrishnu@sandy97'
        }
      });

        var mailOptions = {
        from: 'sk.tech.1997@gmail.com',
        to: 'sk.tech.1997@outlook.com',
        subject: 'No-reply@Ergophile',
        text: 'Hi User! Your password is :'+passDbMail+' .Support, Ergophile @ 2023'
      };

      transporter.sendMail(mailOptions, function(error, info){
      if (error) {
        console.log(error);
        } else {
        console.log('Email sent: ' + info.response);
        }
      })
      toast.success("Password has been sent to registered email", {
        position: toast.POSITION.TOP_CENTER,
      });

        }catch (error) {
          console.error(error);
        }

        }




        
          const {email, password} = formData;

          return (
            afterLogin ? 
            <div className="Login">
          <body>
          <nav>
                    <div className="menu">
                        <div class="logo">
                        <a href="/">Portfolio</a>
                        </div>
                        <ul>
                        <li><a href="/">Home</a></li>
                        <li><a href="/career">Career</a></li>
                        <li><a href="/contact">Contact</a></li>
                        <li><a href="/login">Login</a></li>
                        </ul>
                    </div>
                    </nav>
                    <br></br><br></br><br></br><br></br><br></br>
                    <h1 style={{ color: 'white' }}>Welcome user</h1>
            </body>
            </div> :
          <div className="Login">
          <body>
          <nav>
                    <div className="menu">
                        <div class="logo">
                        <a href="/">Portfolio</a>
                        </div>
                        <ul>
                        <li><a href="/">Home</a></li>
                        <li><a href="/career">Career</a></li>
                        <li><a href="/contact">Contact</a></li>
                        <li><a href="/login">Login</a></li>
                        </ul>
                    </div>
                    </nav>
                    <br></br><br></br><br></br><br></br><br></br>
                    <br></br><br></br><br></br><br></br><br></br>
            <form onSubmit={handleSubmit}>
            <div id="login-btn"><label>Login</label></div>

            <div className="form-control">
              <input
                  type="email"
                  name="email"
                  value={email}
                  onChange={handleChange}
                  placeholder='Email or username'
                />
            </div>
            <div className="form-control">
              <input
                  type="password"
                  name="password"
                  value={password}
                  onChange={handleChange}
                  placeholder='Password'
                />
            </div>
            <div className="form-control"
            style={{fontSize:'15px',display:'flex-inline',
            textAlign:'center',fontWeight:'bold',color:'#000048',
            cursor:'pointer',fontFamily:"'Montserrat', sans-serif"}}
            >
            <p onClick={handleSubmit}>SIGN IN</p>
            </div>
            <div id='forgot-btn'><span onClick={handlePassword}>Forgot password?</span></div>
              <ToastContainer />
            </form>
          </body>
        </div>
          );
        }
