import React from 'react';
import styled from "styled-components";

function Home(props) {

    const Wrapper = styled.div`
    @import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@300&display=swap');

     *{
      margin: 0;
      padding: 0;
      box-sizing: border-box;
      font-family: 'Montserrat', sans-serif;
      font-weight: normal;
    }
    ::selection{
      color: #000;
      background: #fff;
    }
    nav{
      position: fixed;
      background: #1b1b1b;
      width: 100%;
      padding: 10px 0;
      z-index: 12;
    }
    nav .menu{
      max-width: 1250px;
      margin: auto;
      display: flex;
      align-items: center;
      justify-content: space-between;
      padding: 0 20px;
    }
    .menu .logo a{
      text-decoration: none;
      color: #fff;
      font-size: 35px;
      font-weight: 600;
    }
    .menu ul{
      display: inline-flex;
    }
    .menu ul li{
      list-style: none;
      margin-left: 7px;
    }
    .menu ul li:first-child{
      margin-left: 0px;
    }
    .menu ul li a{
      text-decoration: none;
      color: #fff;
      font-size: 18px;
      font-weight: 500;
      padding: 8px 15px;
      border-radius: 5px;
      transition: all 0.3s ease;
    }
    .menu ul li a:hover{
      background: #fff;
      color: black;
    }
    .img{
      background: url('bodyImg.jpg')no-repeat;
      width: 100%;
      height: 100vh;
      background-size: cover;
      background-position: center;
      position: relative;
    }
    .img::before{
      content: '';
      position: absolute;
      height: 100%;
      width: 100%;
      background: rgba(0, 0, 0, 0.4);
    }
    .center{
      position: absolute;
      top: 40%;
      left: 20%;
      transform: translate(-50%, -50%);
      width: 100%;
      padding: 0 20px;
      text-align: center;
      
    }
    .center .title{
      color: #fff;
      font-size: 45px;
      font-weight: 600;
    }
    .center .sub_title{
      color: #fff;
      font-size: 22px;
      font-weight: 400;
    }
    .center .btns{
      margin-top: 20px;
    }
    .center .btns button{
      height: 55px;
      width: 170px;
      border-radius: 5px;
      border: none;
      margin: 0 10px;
      border: 2px solid white;
      font-size: 20px;
      font-weight: 500;
      padding: 0 10px;
      cursor: pointer;
      outline: none;
      transition: all 0.3s ease;
    }
    .center .btns button:first-child{
      color: #fff;
      background: none;
    }
    .btns button:first-child:hover{
      background: white;
      color: black;
    }
    .center .btns button:last-child{
      background: white;
      color: black;
    }
`;



    return (
        <Wrapper>
        <nav>
          <div class="menu">
            <div class="logo">
              <a href="/">Portfolio</a>
            </div>
            <ul>
              <li><a href="/">Home</a></li>
              <li><a href="/blogs">Blogs</a></li>
            <li><a href="/contact">Contact</a></li>
            </ul>
          </div>
        </nav>
        <div class="img"><img src='./images/bodyImg.jpg' alt='bodyImg'/></div>
        <div class="center">
    <div class="title">Sandipan Kundu</div>
    <br></br>
    <ol>
    <li><div class="sub_title">MERN Stack Developer</div></li>
    <li><div class="sub_title">Java Developer</div></li>
    <li><div class="sub_title">API Developer</div></li>
    </ol>
    
    <div class="btns">
      <button>Learn More</button>
      <button>Subscribe</button>
    </div>
  </div>
        </Wrapper>
     );
}


export default Home;
