import React, { useState }  from 'react';
import "./styles.css";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from "axios";

export default function Login(props) {
  var err = false;  
  const [afterLogin, setLoginFlag] = useState(false);   
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const { email, password} = formData;

    if (email === '' || email ===null || password === '' || password ===null) {
      toast.error("Enter the credentials", {
         position: toast.POSITION.TOP_CENTER,
       });
      err = true;
      return false;
     }
   
  const baseURL = "http://localhost:8080/loginProcessing/"+email;
  try {
  const response = await axios.get(baseURL);
  const data = response.data;

  if(data.length ===0) {
    err = true;
    toast.error("No record found", {
      position: toast.POSITION.TOP_CENTER,
    });
  }
 // const emailDb = data[0].user_email;
  const passDb = data[0].user_password;

  // if (email !== emailDb) {
  //  toast.error("Email does not match", {
  //     position: toast.POSITION.TOP_CENTER,
  //   });
  //   err = true;
  // }
  if (password !== passDb) {
    toast.error("Password does not match", {
      position: toast.POSITION.TOP_CENTER,
    });
    err = true;
  }
} catch (error) {
  console.error(error);
}
  if(err===false) setLoginFlag(true);
  return true;
};

  const {email, password} = formData;

  return (
    afterLogin ? 
    <div className="Login">
  <body>
  <nav>
            <div className="menu">
                <div class="logo">
                <a href="/">Portfolio</a>
                </div>
                <ul>
                <li><a href="/">Home</a></li>
                <li><a href="/career">Career</a></li>
                <li><a href="/contact">Contact</a></li>
                <li><a href="/login">Login</a></li>
                </ul>
            </div>
            </nav>
            <br></br><br></br><br></br><br></br><br></br>
            <h1 style={{ color: 'white' }}>Welcome user</h1>
    </body>
    </div> :
  <div className="Login">
  <body>
  <nav>
            <div className="menu">
                <div class="logo">
                <a href="/">Portfolio</a>
                </div>
                <ul>
                <li><a href="/">Home</a></li>
                <li><a href="/career">Career</a></li>
                <li><a href="/contact">Contact</a></li>
                <li><a href="/login">Login</a></li>
                </ul>
            </div>
            </nav>
            <br></br><br></br><br></br><br></br><br></br>
            <br></br><br></br><br></br><br></br><br></br>
     <form onSubmit={handleSubmit}>
     <div id="login-btn"></div>



  <div class="form-outline mb-4">
    <input type="email" id="form2Example1" class="form-control" />
    <label class="form-label" for="form2Example1">Email address</label>
  </div>

 
  <div class="form-outline mb-4">
    <input type="password" id="form2Example2" class="form-control" />
    <label class="form-label" for="form2Example2">Password</label>
  </div>


  <div class="row mb-4">
    <div class="col d-flex justify-content-center">
    
      <div class="form-check">
        <input class="form-check-input" type="checkbox" value="" id="form2Example31" checked />
        <label class="form-check-label" for="form2Example31"> Remember me </label>
      </div>
    </div>

    <div class="col">
     
      <a href="#!">Forgot password?</a>
    </div>
  </div>


  <button type="button" class="btn btn-primary btn-block mb-4">Sign in</button>


  <div class="text-center">
    <p>Not a member? <a href="#!">Register</a></p>
    <p>or sign up with:</p>
    <button type="button" class="btn btn-link btn-floating mx-1">
      <i class="fab fa-facebook-f"></i>
    </button>

    <button type="button" class="btn btn-link btn-floating mx-1">
      <i class="fab fa-google"></i>
    </button>

    <button type="button" class="btn btn-link btn-floating mx-1">
      <i class="fab fa-twitter"></i>
    </button>

    <button type="button" class="btn btn-link btn-floating mx-1">
      <i class="fab fa-github"></i>
    </button>
  </div>
  <ToastContainer />
</form>
</body>
</div>
  );
}
